import React, { useState } from 'react';
import TopNavBar from './TopNav';

const BMICalculator = () => {
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  const [bmi, setBmi] = useState(null);

  const handleWeightChange = e => {
    setWeight(e.target.value);
    if (!e.target.value || !height) {
      setBmi(null);
    }
  };

  const handleHeightChange = e => {
    setHeight(e.target.value);
    if (!e.target.value || !weight) {
      setBmi(null);
    }
  };

  const calculateBMI = (e) => {
    e.preventDefault();
    if (weight && height) {
      const heightInMeters = height / 100; 
      const calculatedBmi = weight / (heightInMeters * heightInMeters);
      setBmi(calculatedBmi.toFixed(2));
    }
  };

  return (
    <div>
      <TopNavBar />
      <div className="bmi-calculator" style={{ display: 'flex', justifyContent: 'center', marginTop: '120px' }}>
        <form onSubmit={calculateBMI} style={{ width: '150px', padding: '20px', border: '1px solid #ccc', borderRadius: '8px'}}>
          <div style={{marginBottom: '20px'}}>
            <label htmlFor="weight">Weight (kg):</label>
            <input
              type="number"
              id="weight"
              value={weight}
              onChange={handleWeightChange}
              style={{ width: '100%', marginTop: '5px' }}
            />
          </div>
          <div style={{marginBottom: '20px'}}>
            <label htmlFor="height">Height (cm):</label>
            <input
              type="number"
              id="height"
              value={height}
              onChange={handleHeightChange}
              style={{ width: '100%', marginTop: '5px' }}
            />
          </div>
          <button type="submit" style={{ width: '100%' }}>Calculate BMI</button>
        </form>
        {bmi && (
          <div style={{marginTop: '20px', textAlign: 'center'}}>
            <h3>Your BMI is: {bmi}</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default BMICalculator;
